mapper.sql:
select p.creditBillId, p.creditCard, p.billDate, p.imburseDate, p.cnyImburseAmount, p.cnyLeastAmount, p.usdImburseAmount, p.usdLeastAmount, decode(p.isClear,'Y','是','否') isClear, p.bankName, p.billYear, p.billMonth, servicePhone from ( select * from (select a.CREDIT_BILL_ID creditBillId, a.CREDIT_CARD creditCard, a.BILL_DATE billDate, a.IMBURSE_DATE imburseDate, a.CNY_IMBURSE_AMOUNT cnyImburseAmount, a.CNY_LEAST_AMOUNT cnyLeastAmount, a.USD_IMBURSE_AMOUNT usdImburseAmount, a.USD_LEAST_AMOUNT usdLeastAmount, a.IS_CLEAR isClear, b.bank_name bankName, to_char(a.bill_date, 'yyyy') billYear, to_char(a.bill_date, 'mm') billMonth, b.service_phone servicePhone from fms_bank_credit_bill_info a, fms_bank_credit_card_info b where a.credit_card = b.card_number and a.is_clear = 'N' order by a.imburse_date asc) union all select * from (select a.CREDIT_BILL_ID creditBillId, a.CREDIT_CARD creditCard, a.BILL_DATE billDate, a.IMBURSE_DATE imburseDate, a.CNY_IMBURSE_AMOUNT cnyImburseAmount, a.CNY_LEAST_AMOUNT cnyLeastAmount, a.USD_IMBURSE_AMOUNT usdImburseAmount, a.USD_LEAST_AMOUNT usdLeastAmount, a.IS_CLEAR isClear, b.bank_name bankName, to_char(a.bill_date, 'yyyy') billYear, to_char(a.bill_date, 'mm') billMonth, b.service_phone servicePhone from fms_bank_credit_bill_info a, fms_bank_credit_card_info b where a.credit_card = b.card_number and a.is_clear = 'Y' order by a.imburse_date desc)
) p where 1 = 1
and p.creditCard = #{requestObject.creditCard,jdbcType=VARCHAR}
and p.bankName = #{requestObject.bankName,jdbcType=VARCHAR}
and p.billYear = #{requestObject.billYear}
and p.billMonth = #{requestObject.billMonth}

mapper.select:
        select a.credit_detail billContent
	    from fms_bank_credit_bill_info a
	    where a.credit_bill_id = #{requestObject.creditBillId}
